export const validateEmail=t=>/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(t);export const validatePassword=t=>t.length>=8;export const showError=(t,s,e)=>{s.textContent=e,s.style.display="block",t.classList.add("invalid")};export const clearError=(t,s)=>{s.textContent="",s.style.display="none",t.classList.remove("invalid")};